DROP TABLE IF EXISTS flights CASCADE;

CREATE TABLE IF NOT EXISTS flights (
    FL_DATE VARCHAR(100) NOT NULL,
    AIRLINE VARCHAR(100),
    AIRLINE_DOT VARCHAR(100),
    AIRLINE_CODE VARCHAR(10),
    DOT_CODE INT,
    FL_NUMBER INT,
    ORIGIN VARCHAR(10),
    ORIGIN_CITY VARCHAR(100),
    DEST VARCHAR(10),
    DEST_CITY VARCHAR(100),
    CRS_DEP_TIME INT,
    DEP_TIME FLOAT,
    DEP_DELAY FLOAT,
    TAXI_OUT FLOAT,
    WHEELS_OFF FLOAT,
    WHEELS_ON FLOAT,
    TAXI_IN FLOAT,
    CRS_ARR_TIME INT,
    ARR_TIME FLOAT,
    ARR_DELAY FLOAT,
    CANCELLED FLOAT,
    CANCELLATION_CODE VARCHAR(1),
    DIVERTED FLOAT,
    CRS_ELAPSED_TIME FLOAT,
    ELAPSED_TIME FLOAT,
    AIR_TIME FLOAT,
    DISTANCE FLOAT,
    DELAY_DUE_CARRIER FLOAT,
    DELAY_DUE_WEATHER FLOAT,
    DELAY_DUE_NAS FLOAT,
    DELAY_DUE_SECURITY FLOAT,
    DELAY_DUE_LATE_AIRCRAFT FLOAT
);
